var fs = require('fs')

function doStuff( callback ) {
    fs.readFile(process.argv[2], function doneReading( err, data ){
        if (err) console.log("There is an error: " + err );
        let contents = data;
        let lines = data.toString().split('\n').length-1;
        callback( lines );
    });
    
}
function callback( lines ) {
    console.log(lines);
}

doStuff( callback );
