let fs = require("fs");

function listfiles( callback ) {
    fs.readdir( process.argv[2], function doneScanning(err, data) {
       callback(data); 
    });
}

function callback( content ) {
    // let retval = "";
    for( let i = 0; i < content.length; ++i) {
        // console.log(content[i] + ": type = " + typeof(content[i]) + " \n");
        if ( content[i].endsWith("." + process.argv[3]) ) {
            console.log( content[i]);
        }
    }
}

listfiles( callback );

