let http = require("http");
let url = process.argv[2];

let myData = http.get( url, function( response ) {
    response.on('error', function(err) {
        console.error( 'Error: ' + err);
    });
    response.setEncoding('utf8').on( 'data', function( data ) {
        console.log(data);
    });
});

