let http = require("http");
let bl = require("bl");

http.get( process.argv[2], function( response ) {
    response.pipe( bl( function ( err, data) {
        console.log(data.length);
        data = data.toString();
        
        console.log(data);
    } ) );
    
}).on('error', console.error)


